// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../node_modules/@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * A basic token for testing the HashedTimelockERC20.
 */
contract CheckAmount {
    function check(
        address _token,
        address _sender,
        uint256 _amount
    ) public view returns (bool isTransfer, uint256 amount) {
        return (
            ERC20(_token).allowance(_sender, address(this)) >= _amount,
            ERC20(_token).allowance(_sender, address(this))
        );
    }
}
